import { listDecks } from "../utils/api";

export async function getDecks() {
  const abortController = new AbortController();
  let decks = [];
  try {
    decks = await listDecks(abortController.signal);
  } catch (error) {
    if (error.name === "AbortError") {
      console.log("Aborted", decks);
    } else {
      throw error;
    }
    return decks;
  }
}
