import React from "react";
import { Link, Switch, Route } from "react-router-dom";

export default function ListDeckItems(props) {
  const { decks, setDecks } = props;

  const deleteDeck = (itemId) => {
    setDecks(decks.filter((deck) => deck.id !== itemId));
  };

  return (
    <section>
      {decks.map((deck, index) => {
        return (
          <div key={index} className="card my-3">
            <div className="card-body">
              <h5 className="card-title">
                {deck.name}
                <span className="float-right">{`${deck.cards.length} cards`}</span>{" "}
              </h5>
              <p className="card-text">{deck.description}</p>
              <div>
                <Link to="#" className="btn btn-secondary float-left">
                  View
                </Link>
                <Link to="#" className="btn btn-primary mx-2">
                  Study
                </Link>
                <span className="float-right">
                  <Link
                    to="#"
                    className="btn btn-danger"
                    onClick={() =>
                      setDecks(decks.filter((el) => deck.id !== el.id))
                    }
                  >
                    Delete
                  </Link>
                </span>
              </div>
            </div>
          </div>
        );
      })}
    </section>
  );
}
