import React from "react";
import { DisplayCards } from "./DisplayCards";
import { Link } from "react-router-dom";
import { listCards } from "../utils/api/index";

export default function ListDeckItems(props) {
  const { decks } = props;

  return (
    <section>
      {decks.map((deck) => {
        return (
          <div class="card my-3">
            <div class="card-body">
              <h5 class="card-title">
                {deck.name}
                <span class="float-right">{`${listCards(deck.id)} cards`}</span>{" "}
              </h5>
              <p class="card-text">{deck.description}</p>
              <div>
                <Link to="#" class="btn btn-secondary float-left">
                  View
                </Link>
                <Link to="#" class="btn btn-primary mx-2">
                  Study
                </Link>
                <span class="float-right">
                  <Link to="#" class="btn btn-danger">
                    Delete
                  </Link>
                </span>
              </div>
            </div>
          </div>
        );
      })}
    </section>
  );
}
