import React, { useEffect, useState } from "react";
import { useParams } from "react-router";

export default function CreateCard(props) {
  const { deckId, cardId } = useParams();
  const { newItem, isDeck } = props;
  const [formNames, setFormNames] = useState({
    labelFirst: "",
    labelSecond: "",
    titleNewOrEdit: "",
    titleCardOrDeck: "",
  });

  useEffect(() => {
    isDeck
      ? newItem
        ? setFormNames({
            titleNewOrEdit: "New",
            labelFirst: "Name",
            labelSecond: "Description",
            titleCardOrDeck: "Deck",
          })
        : setFormNames({
            titleNewOrEdit: "Edit",
            labelFirst: "Name",
            labelSecond: "Description",
            titleCardOrDeck: "Deck",
          })
      : newItem
      ? setFormNames({
          titleNewOrEdit: "New",
          labelFirst: "Front",
          labelSecond: "Back",
          titleCardOrDeck: "Card",
        })
      : setFormNames({
          titleNewOrEdit: "Edit",
          labelFirst: "Front",
          labelSecond: "Back",
          titleCardOrDeck: "Card",
        });
  }, [newItem, isDeck]);

  function textAreaOrNot(whatIsIt, formNames) {
    if (whatIsIt) {
      return <div className="form-group"></div>;
    } else {
    }

    return;
  }

  console.log(formNames);
  return (
    <section className="container">
      <h1>{`${formNames.titleNewOrEdit} ${formNames.titleCardOrDeck}`}</h1>
      <form>
        {textAreaOrNot(isDeck, formNames)}
        <div className="form-group">
          <label for="secondTextArea">{formNames.labelSecond}</label>
          <textarea className="form-control" id="secondTextArea"></textarea>
        </div>
      </form>
    </section>
  );
}
