import React, { useEffect, useState } from "react";
import { listDecks, listCards } from "../utils/api";
import ListDeckItems from "./ListDeckItems";

function DisplayCards(props) {
  const [decks, setDecks] = useState([]);
  const [cards, setCards] = useState([]);
  const [cardsAndDecks, setCardsAndDecks] = useState([]);
  useEffect(() => {
    const abortController = new AbortController();
    async function setup() {
      try {
        await listDecks(abortController.signal)
          .then((elements) => setDecks(elements))
          .then(
            setCards(
              decks.map((element) => {
                const cardsById = listCards(element.id, abortController.signal);
                console.log(cardsById);
                return cardsById;
              })
            )
          );
      } catch (error) {
        if (error.name === "AbortError") {
          console.log("Aborted", decks);
        } else {
          throw error;
        }
      }
    }
  }, []);

  return (
    <div>
      <div>
        <button className="btn btn-secondary m-3">Create Deck</button>
      </div>
      <div className="container">
        <ListDeckItems decks={decks} cards={cards} />
      </div>
    </div>
  );
}

export default DisplayCards;
