import React, { useEffect, useState } from "react";
import { listDecks, listCards } from "../utils/api";
import ListDeckItems from "./ListDeckItems";

function DisplayCards(props) {
  const [decks, setDecks] = useState([]);

  const [cardsAndDecks, setCardsAndDecks] = useState([]);
  useEffect(() => {
    const abortController = new AbortController();
    try {
      listDecks(abortController.signal).then((elements) => setDecks(elements));
    } catch (error) {
      if (error.name === "AbortError") {
        console.log("Aborted", decks);
      } else {
        throw error;
      }
    }
  }, []);

  return (
    <div>
      <div>
        <button className="btn btn-secondary m-3">Create Deck</button>
      </div>
      <div className="container">
        <ListDeckItems decks={decks} />
      </div>
    </div>
  );
}

export default DisplayCards;
