import React, { setState } from "react";
import { useParams } from "react-router";

export default function CreateCard(props) {
  const { deckId, cardId } = useParams();
  const { newItem, isDeck } = props;
  cosnt[(formNames, setFormNames)] = setState({
    labelFirst: "",
    labelSecond: "",
  });

  isDeck
    ? setFormNames({ labelFirst: "Name", labelSecond: "Description" })
    : setFormNames({ labelFirst: "Front", labelSecond: "Back" });

  return (
    <form>
      <div className="form-group">
        <label for="first"></label>
        <textarea className="form-control" id="first"></textarea>
      </div>
    </form>
  );
}
