import React, { useState, useEffect } from "react";
import Header from "./Header";
import NotFound from "./NotFound";
import DisplayCards from "./DisplayCards";
import { Switch, Route } from "react-router-dom";
import { Fragment } from "react";
import StudyDeck from "./StudyDeck";
import { listDecks } from "../utils/api";
import Form from "./Form";
import Deck from "./Deck";

function Layout() {
  const [decks, setDecks] = useState([]);

  useEffect(() => {
    const abortController = new AbortController();

    try {
      listDecks(abortController.signal).then((elements) => setDecks(elements));
    } catch (error) {
      if (error.name === "AbortError") {
        console.log("Aborted", decks);
      } else {
        throw error;
      }
    }
  });

  return (
    <Fragment>
      <Header />
      <div className="container">
        <Switch>
          <Route exact path="/">
            <DisplayCards decks={decks} setDecks={setDecks} />
          </Route>
          <Route path="/decks/:deckId/study">
            <StudyDeck />
          </Route>
          <Route path="/decks/new">
            <Form newItem={true} isDeck={true} />
          </Route>
          <Route exact path="/decks/:deckId">
            <Deck />
          </Route>
          <Route path="/decks/:deckId/edit">
            <Form newItem={false} isDeck={true} />
          </Route>
          <Route path="/decks/:deckId/cards/new">
            <Form newItem={true} isDeck={false} />
          </Route>
          <Route path="/decks/:deckId/cards/:cardId/edit">
            <Form newItem={false} isDeck={false} />
          </Route>
          <Route>
            <NotFound />
          </Route>
        </Switch>
      </div>
    </Fragment>
  );
}

export default Layout;
