import React, { useEffect, useState } from "react";
import { useParams, useHistory } from "react-router";
import FormBreadcrumb from "./FormBreadcrumb";
import {
  readDeck,
  readCard,
  updateCard,
  updateDeck,
  createCard,
  createDeck,
} from "../utils/api";

export default function CreateCard(props) {
  const history = useHistory();
  const { deckId, cardId } = useParams();
  const { newItem, isDeck } = props;
  const [deck, setDeck] = useState({ cards: [] });
  const [card, setCard] = useState({});
  const [inputsFromForm, setInputsFromForm] = useState({
    firstInput: "",
    secondInput: "",
  });
  const [formNames, setFormNames] = useState({
    labelFirst: "",
    labelSecond: "",
    titleNewOrEdit: "",
    titleCardOrDeck: "",
  });

  useEffect(() => {
    const abortController = new AbortController();
    if (deckId) {
      try {
        readDeck(deckId, abortController.signal).then((element) => {
          setDeck(element);
        });
      } catch (error) {
        if (error.name === "AbortError") {
          console.log("Aborted", deck);
        } else {
          throw error;
        }
      }
    }
    if (cardId) {
      try {
        readCard(cardId, abortController.signal).then((element) => {
          setCard(element);
        });
      } catch (error) {
        if (error.name === "AbortError") {
          console.log("Aborted", card);
        } else {
          throw error;
        }
      }
    }
  }, []);

  useEffect(() => {
    isDeck
      ? newItem
        ? setFormNames({
            titleNewOrEdit: "Create",
            labelFirst: "Name",
            labelSecond: "Description",
            titleCardOrDeck: "Deck",
          })
        : setFormNames({
            titleNewOrEdit: "Edit",
            labelFirst: "Name",
            labelSecond: "Description",
            titleCardOrDeck: "Deck",
          })
      : newItem
      ? setFormNames({
          titleNewOrEdit: "New",
          labelFirst: "Front",
          labelSecond: "Back",
          titleCardOrDeck: "Card",
        })
      : setFormNames({
          titleNewOrEdit: "Edit",
          labelFirst: "Front",
          labelSecond: "Back",
          titleCardOrDeck: "Card",
        });
  }, [isDeck, newItem]);

  function submitHandler(event) {
    console.log("Running");
    event.preventDefault();
    if (newItem && isDeck) {
      createDeck({
        name: inputsFromForm.firstInput,
        description: inputsFromForm.secondInput,
      });
      history.push(`/`);
    } else if (!newItem && isDeck) {
      updateDeck({
        name: inputsFromForm.firstInput,
        description: inputsFromForm.secondInput,
        id: deckId,
      });
      history.push(`/decks/${deckId}`);
    } else if (newItem && !isDeck) {
      createCard(deckId, {
        front: inputsFromForm.firstInput,
        back: inputsFromForm.secondInput,
      });
      history.go(0);
    } else {
      updateCard({
        ...card,
        front: inputsFromForm.firstInput,
        back: inputsFromForm.secondInput,
      });
    }
  }

  function textAreaOrNot(whatIsIt, formNames) {
    if (whatIsIt) {
      return (
        <div className="form-group">
          <label for="deckName">{formNames.labelFirst}</label>
          <input
            placeholder="Deck Name"
            className="form-control"
            type="text"
            id="deckName"
            onChange={(event) =>
              setInputsFromForm({
                ...inputsFromForm,
                firstInput: event.target.value,
              })
            }
          />
        </div>
      );
    } else {
      return (
        <div className="form-group">
          <label for="firstTextArea">{formNames.labelFirst}</label>
          <textarea
            className="form-control"
            id="firstTextArea"
            placeholder="Front side of card"
            onChange={(event) =>
              setInputsFromForm({
                ...inputsFromForm,
                firstInput: event.target.value,
              })
            }
          ></textarea>
        </div>
      );
    }
  }

  return (
    <section className="container">
      <FormBreadcrumb
        newItem={newItem}
        isDeck={isDeck}
        deckId={deckId}
        cardId={cardId}
        deck={deck}
        card={card}
      />
      <h1>{`${formNames.titleNewOrEdit} ${formNames.titleCardOrDeck}`}</h1>
      <form>
        {textAreaOrNot(isDeck, formNames)}
        <div className="form-group">
          <label for="secondTextArea">{formNames.labelSecond}</label>
          <textarea
            className="form-control"
            id="secondTextArea"
            placeholder={isDeck ? "Description of deck" : "Back side of card"}
            onChange={(event) =>
              setInputsFromForm({
                ...inputsFromForm,
                secondInput: event.target.value,
              })
            }
          ></textarea>
        </div>
        <div>
          <button className="btn btn-secondary">
            {newItem ? "Done" : "Cancel"}
          </button>
          <button
            className="btn btn-primary mx-2"
            onClick={(event) => submitHandler(event)}
          >
            {newItem ? "Save" : "Submit"}
          </button>
        </div>
      </form>
    </section>
  );
}
