import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { listDecks, readDeck } from "../utils/api";
import ListDeckItems from "./ListDeckItems";

function DisplayCards(props) {
  const [decks, setDecks] = useState([]);
  const [decksAndCards, setDecksAndCards] = useState([]);

  useEffect(() => {
    const abortController = new AbortController();
    try {
      listDecks(abortController.signal).then((elements) => setDecks(elements));
      setDecksAndCards(
        decks.map((element) => {
          return readDeck(element.id, abortController.signal);
        })
      );
      console.log(decksAndCards);
    } catch (error) {
      if (error.name === "AbortError") {
        console.log("Aborted", decks);
      } else {
        throw error;
      }
    }
  }, []);

  return (
    <div>
      <div>
        <button className="btn btn-secondary m-3">Create Deck</button>
      </div>
      <div className="container">
        <ListDeckItems decks={decks} cards={cards} setCards={setCards} />
      </div>
    </div>
  );
}

export default DisplayCards;
